const dropdownInit=()=>{document.addEventListener("click",e=>{let t;e.target.closest("[data-dropdown]")&&(t=e.target.closest("[data-dropdown]")).classList.toggle("active"),document.querySelectorAll("[data-dropdown].active").forEach(e=>{e!==t&&e.classList.remove("active")})});var e=document.querySelectorAll("[data-dropdown]");992<window.innerWidth&&e.forEach(e=>{e.addEventListener("mouseover",()=>{e.classList.add("active")}),e.addEventListener("mouseleave",()=>{e.classList.remove("active")})})};